mapper.select:
SELECT rs.raid_schedule_seq, rs.raid_code, rs.character_seq, rs.raid_title, rs.raid_captain_character, rs.raid_invite_character, DATE_FORMAT(rs.raid_invite_date, '%Y-%m-%d %k:%i:%s') raid_invite_date, DATE_FORMAT(rs.raid_start_date, '%Y-%m-%d %k:%i:%s') raid_start_date, DATE_FORMAT(rs.raid_clear_date, '%Y-%m-%d %k:%i:%s') raid_clear_date, rs.memo, rs.profit, rs.expense FROM raid_schedule rs, (SELECT rt.raid_code, rt.raid_name, rp.raid_start_date, rp.raid_end_date, rt.raid_sort FROM raid_type rt LEFT JOIN raid_period rp ON rt.raid_code = rp.raid_code AND #{searchDate} BETWEEN raid_start_date AND raid_end_date WHERE 1 =1
AND rt.raid_code = #{raidCode}
) raid WHERE 1 = 1 AND rs.raid_start_date between raid.raid_start_date and raid.raid_end_date AND rs.raid_code = raid.raid_code
AND rs.character_seq IN ( SELECT character_seq FROM user_character WHERE user_seq = #{userSeq})
AND rs.character_seq = #{characterSeq}

SELECT SUM(sch.cnt) as total_cnt
FROM (

   SELECT count(raid_schedule_seq) cnt
     FROM raid_schedule
    WHERE character_seq IN ( SELECT character_seq FROM user_character WHERE user_seq = #{userSeq} )
      AND raid_code = #{period.raidCode}
      AND raid_start_date BETWEEN #{period.raidStartDate} AND #{period.raidEndDate}

) sch
